import { useForm } from "@mantine/form";
import { TextInput, PasswordInput, Select, Button, Box } from "@mantine/core";
import axios from "axios";

type RegisterFormValues = {
  name: string;
  email: string;
  type: string;
  password: string;
  confirmPassword: string;
};

const RegisterPage = () => {
  const form = useForm<RegisterFormValues>({
    initialValues: {
      name: "",
      email: "",
      type: "",
      password: "",
      confirmPassword: "",
    },

    validate: {
      name: (value) =>
        value.trim().length >= 2 ? null : "Tên phải có ít nhất 2 ký tự!",

      email: (value) =>
        /^\S+@\S+\.\S+$/.test(value) ? null : "Email không hợp lệ!",

      type: (value) =>
        value ? null : "Vui lòng chọn loại tài khoản!",

      password: (value) =>
        /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(
          value
        )
          ? null
          : "Mật khẩu phải ≥8 ký tự, gồm chữ hoa, chữ thường, số và ký tự đặc biệt!",

      confirmPassword: (value, values) =>
        value === values.password ? null : "Mật khẩu nhập lại không khớp!",
    },
  });

  // Gọi API
  const handleSubmit = async (values: RegisterFormValues) => {
    try {
      const res = await axios.post("http://localhost:9000/users/register", values);
      console.log("Đăng ký thành công:", res.data);
      alert("Đăng ký thành công!");
    } catch (error) {
      console.error("Lỗi đăng ký:", error);
      alert("Đăng ký thất bại!");
    }
  };

  return (
    <Box maw={400} mx="auto">
      <h1>Đăng ký tài khoản</h1>

      <form onSubmit={form.onSubmit(handleSubmit)}>
        <TextInput
          label="Họ và tên"
          placeholder="Nhập tên"
          {...form.getInputProps("name")}
          mt="sm"
        />

        <TextInput
          label="Email"
          placeholder="example@gmail.com"
          {...form.getInputProps("email")}
          mt="sm"
        />

        <Select
          label="Loại tài khoản"
          placeholder="Chọn loại tài khoản"
          data={[
            { value: "ADMIN", label: "Admin" },
            { value: "USER", label: "User" },
            { value: "DOCTOR", label: "Doctor" },
          ]}
          {...form.getInputProps("type")}
          mt="sm"
        />

        <PasswordInput
          label="Mật khẩu"
          placeholder="Nhập mật khẩu mạnh"
          {...form.getInputProps("password")}
          mt="sm"
        />

        <PasswordInput
          label="Nhập lại mật khẩu"
          placeholder="Xác nhận mật khẩu"
          {...form.getInputProps("confirmPassword")}
          mt="sm"
        />

        <Button type="submit" fullWidth mt="lg">
          Đăng ký
        </Button>
      </form>
    </Box>
  );
};

export default RegisterPage;
