import { Button, PasswordInput, TextInput } from '@mantine/core'
import { IconHeart, IconHeartbeat } from '@tabler/icons-react'
import React from 'react'
import { useForm } from '@mantine/form';
const LoginPage = () => {
    const form = useForm({
    mode: 'uncontrolled',
    initialValues: {
      email: '',
      password: '',
    },

    validate: {
      email: (value) => (/^\S+@\S+$/.test(value) ? null : 'Invalid email'),
      password:(value)=>(!value?"Password is required":null)
    },
  });

    return (
        <div style={{ background: 'url("/bglogin.jpg")' }}
            className='flex flex-col h-screen w-screen !bg-cover !bg-center !bg-no-repeat items-center justify-center'>
            <div className="py-3 text-pink-500 flex gap-1 items-center">
                <IconHeartbeat size={45} stroke={2.5} />
                <span className='font-heading font-semibold text-4xl'>QM</span>
            </div>
            <div className="w-[450px] backdrop-blur-md p-10 py-8 rounded-lg">
                <form className='flex flex-col gap-3 [&_input]:placeholder-neutral-100 [&_.mantine-Input-input]:!border-white focus-within:[&_.mantine-Input-input]:!border-pink-500 [&_.mantine-Input-input]:!border [&_input]:!pl-2 [&_input]:!text-white [&_svg]:text-white'>
                    <div className='self-center font-heading font-medium text-white text-xl'>Login</div>
                    <TextInput {...form.getInputProps('email')} variant='unstyled' radius="md"
                        withAsterisk error=""
                        placeholder="Email" />
                    <PasswordInput {...form.getInputProps('email')} variant="unstyled" radius="md" withAsterisk placeholder="Password" />
                    <Button type='submit' color='pink'>Login</Button>
                </form>
            </div>
        </div>
    )
}

export default LoginPage
